var functions = function(){
	var func = this
	func.pickSomething = function(howMany,fromWhere,splitByWhat){
		if (howMany > 1){
			var chosenOnes = []
			var pick = fromWhere.split(splitByWhat)
			for (var i = 0; i < howMany; i++){
				fromWhere = pick[Math.floor(Math.random() * pick.length)]
				chosenOnes.push(fromWhere)
				pick.splice(pick.indexOf(fromWhere),1)
				}
			return chosenOnes
		} else {
			var pick = fromWhere.split(splitByWhat)
			fromWhere = pick[Math.floor(Math.random() * pick.length)]
			return fromWhere
		}
	}

	func.getStats = function(){
		var stat = 0
		while (stat < 9){
		stat = 0
		var die = [];
			for (var i = 1; i < 5; i++){
				die.push(Math.floor(Math.random() * 6) + 1)
			}
			die.sort().shift()
			die.forEach(function(number){
				stat += number;
			})
		}
		return stat
	}

	func.modifiers = function(stat){
		  if (stat === 9){
		  	return "-1"
		  } else if(stat === 10 || stat === 11){
		  	return "+0"
		  } else if (stat === 12 || stat === 13){
		  	return "+1"
		  } else if (stat === 14 || stat === 15){
		  	return "+2"
		  } else if (stat === 16 || stat === 17){
		  	return "+3"
		  } else if (stat === 18 || stat === 19){
		  	return "+4"
		  } else {
		  	return "+5"
		  }
	}

	func.shuffle = function(numbers){
	    for(var j, x, i = numbers.length; i; j = Math.floor(Math.random() * i), x = numbers[--i], numbers[i] = numbers[j], numbers[j] = x);
	    return numbers;
	}
}

module.exports = functions;